int compute0(int mn, int mx)
{
    int ret = mx - mn + 1;
    for(int i = 2; i * i <= mx; i++) {
        int k = i*i;
        ll start = mn / k;
        if(start * k < mn) start++;
        start *= k;
        while(start <= mx) {
            if(!chk[start - mn]) {
                chk[start - mn] = true;
                ret--;
            }
            start += k;
        }
    }

    for(int i = 2; i * i <= mx; i++) {
        int k = i*i;
        ll start = mn / k;
        if(start * k < mn) start++;
        start *= k;
        while(start <= mx) {
            chk[start - mn] = false;
            start += k;
        }
    }

    return ret;
}

inline int compute(int x)
{
    int ret = 0;
    int idx = x / B;
    ret += cache[idx];
    ret += compute0(idx * B, x);
    return ret;
}

int main()
{
    int K;
    cin >> K;
    ll lo = 0;
    ll hi = MAXN;
    while(lo + 1 < hi) {
        ll mid = (lo + hi) / 2;
        if(compute(mid) < K) lo = mid;
        else hi = mid;
    }
    cout << hi << "\n";
}